import React, { Component } from 'react'
import { View, ScrollView, StatusBar, Platform, StyleSheet, SafeAreaView, Dimensions, KeyboardAvoidingView, FlatList, Switch, Modal} from 'react-native';
import Text from '../components/Txt'
import MapInput, { PROVIDER_GOOGLE } from '../components/MapInput'
import { GooglePlacesAutocomplete } from 'react-native-google-places-autocomplete';
import MapView, {Marker} from 'react-native-maps';
import DayMap from '../constants/DayMap'
import NightMap from '../constants/NightMap'
import { KeyboardAwareScrollView } from 'react-native-keyboard-aware-scroll-view'
import {Card} from 'react-native-paper';

import ImageBrowser from '../features/camera-roll/ImageBrowser'


import Input from '../components/Input'
import Icon from '../components/Icon'
import Button from '../components/Button'
import Colors from '../constants/Colors'
import { LinearGradient } from 'expo-linear-gradient'

import * as firebase from 'firebase'
import 'firebase/firestore';
import firebaseConfig from '../firebaseConfig'


//MobX Imports
import {inject, observer} from 'mobx-react/native'
import { requireNativeViewManager } from '@unimodules/core';

// Stripe Payments
import stripe from 'tipsi-stripe'
stripe.setOptions({
  publishableKey: 'pk_test_lojsrOCvzrsIiGQFXSUquLHX00pzpkST7r',
  // merchantId: 'MERCHANT_ID', // Optional
  // androidPayMode: 'test', // Android only
})





@inject("UserStore", "ComponentStore")
@observer
class addPayment extends Component {
  _isMounted = false;

  static navigationOptions = {
    title: "Add Your Driveway",
    headerTitleStyle:{
        fontWeight: "300",
        fontSize: 18,
    }

};


    constructor(props){
        super(props)

        this.state = {
            region: {
              latitude: null,
              longitude: null,
              latitudeDelta: null,
              longitudeDelta: null,
            },
            address: {
              full: null,
              number: null,
              street: null,
              box: null,
              city: null,
              county: null,
              state: null,
              state_abbr: null,
              country: null,
              country_abbr: null,
              zip: null,
            },
            allValid: false,
