import React from 'react';
import {Platform, Text, StatusBar, StyleSheet, View, ScrollView, SafeAreaView} from 'react-native'
// import Text from '../../components/Txt'

class PrivacyPolicy extends React.Component{
    componentDidMount(){
        // Set Status Bar page info here!
       this._navListener = this.props.navigation.addListener('didFocus', () => {
           StatusBar.setBarStyle('dark-content', true);
           Platform.OS === 'android' && StatusBar.setBackgroundColor('white');
         });
       }

    render(){
        return(
            <ScrollView>
                <View style={styles.container}>
                    <Text style={styles.para}>
                    Riive, Inc. (“Riive,” “we,” “us”) takes your privacy very seriously. We provide this Privacy Policy to inform you of our policies and procedures regarding the collection, use and disclosure of personal information we receive from Users of our website at Riive.com (the “Site”) and the Riive iOS mobile device application (the “App”) or any other products provided by Riive, Inc. The Site, App, and all other Riive services are collectively referred to as the “Services.” This Privacy Policy applies only to information that you provide to us through use of the Services or by direct contact with a representative of Riive. By using the Services, you are agreeing to be bound by these policies. Our privacy policy will be updated as necessary. We will notify you of any material changes and all updates to the privacy policy will be posted on the Site and within the Mobile App.</Text>
                    <Text style={styles.head}>Key Terms</Text>
                    <Text style={styles.para}><Text style={styles.underline}>Services</Text>: The website at Riive.com (the “Site”) and the Riive iOS mobile device application (the “App”) or any other products provided by Riive, LLC. The Site, App, and all other Riive services are collectively referred to as the “Services.”</Text>
                    <Text style={styles.para}><Text style={styles.underline}>User</Text>: Any individual who is in contact with the Services, whether registered or not.</Text>
                    <Text style={styles.para}><Text style={styles.underline}>Registered User</Text>: Any individual who has contacted the Services, provided the required information, and completed the steps necessary to create an account.</Text>
                    <Text style={styles.para}><Text style={styles.underline}>Host</Text>: Any Registered User who has created and published a Listing in the Services whether the owner, renter, or employee of the owner of that property.</Text>
                    <Text style={styles.para}><Text style={styles.underline}>Guest</Text>: Any Registered User who uses the Services to seek a parking space, request to utilize a parking space, or complete an exchange of funds with a Host for use of property.</Text>
                    <Text style={styles.para}><Text style={styles.underline}>Listing</Text>: A property which has been input into the Services and offered by a Host for a Guest to utilize.</Text>
